        scriptString[1] = "\nArrayCreateFromList(list):=\n[\n  Local(result,i);\n  result:=ArrayCreate(Length(list),0);\n  i:=1;\n  While (list !=? {})\n  [\n    result[i]:=First(list);\n    i++;\n    list:=Rest(list);\n  ];\n  result;\n];\n\n";
