        scriptString[1] = "\nFunction(\"HoldArgumentNumber\",{function,arity,index})\n[\n  Local(args);\n  args:=RulebaseArgumentsList(function,arity);\n/* Echo({\"holdnr \",args}); */\n  ApplyFast(\"HoldArgument\",{function,args[index]});\n];\n\n";
