        scriptString[1] = "\nEquationLeft(_symbolicEquation)_(Type(symbolicEquation) =? \"==\")  <--\n[\n    Local(listForm);\n    \n    listForm := FunctionToList(symbolicEquation);\n    \n    listForm[2];\n];\n";
