        scriptString[1] = "\nEquationRight(_symbolicEquation)_(Type(symbolicEquation) =? \"==\")  <--\n[\n    Local(listForm);\n    \n    listForm := FunctionToList(symbolicEquation);\n    \n    listForm[3];\n];\n\n";
