        scriptString[0] = null;
        scriptString[1] = "\n// This is so YacasFactor(Sin(x)) doesn't return FWatom(Sin(x))\n//Factor(_p) <-- FW(Factors(p));\n10 # Factor(p_CanBeUni) <-- FW(Factors(p));\n\n";
