        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\nFunction(\"DiagonalMatrix\",{list})\n[\n  Local(result,i,n);\n  n:=Length(list);\n  result:=Identity(n);\n  For(i:=1,i<=?n,i++)\n  [\n    result[i][i] := list[i];\n  ];\n  result;\n];\n\n";
