        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\nHilbertInverseMatrix(n):=GenMatrix({{i,j},\n        (-1)^(i+j)*(i+j-1)*BinomialCoefficient(n+i-1,n-j)*BinomialCoefficient(n+j-1,n-i)*BinomialCoefficient(i+j-2,i-1)^2},n,n);\n\n";
