        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\nIdentity(n_IsNonNegativeInteger) <--\n[\n    Local(i,result);\n    result:={};\n    For(i:=1,i<=?n,i++)\n    [\n      DestructiveAppend(result,BaseVector(i,n));\n    ];\n    result;\n];\n\n";
