        scriptMap.put("Where",scriptString);

        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\nRulebase(\"<-\",{left,right});\nHoldArgument(\"<-\",left);\nHoldArgument(\"<-\",right);\n\n";
