        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\nMM(_expr) <--  MM(expr,MultiExpressionList(expr));\nMM(_expr,_vars) <--  MakeMultiNomial(expr,vars);\n\n";
