        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\n5  # IsComposite(1)                        <-- False;\n10 # IsComposite(n_IsPositiveInteger)         <-- (Not? IsPrime(n));\n\n";
        scriptString[2] = "/org/mathpiper/scripts4/numbers/IsComposite.mpw";
