        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\n5  # IsCoprime(list_IsList)                     <-- (Lcm(list) =? Product(list));\n10 # IsCoprime(n_IsInteger,m_IsInteger)        <-- (Gcd(n,m) =? 1);\n\n";
