        scriptString[0] = null;
        scriptString[1] = "\n/* Returns whether n is a prime^m. */\n10 # IsPrimePower(n_IsPrime) <-- True;\n10 # IsPrimePower(0) <-- False;\n10 # IsPrimePower(1) <-- False;\n20 # IsPrimePower(n_IsPositiveInteger) <-- (GetPrimePower(n)[2] >? 1);\n\n";
