        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\n/// obtain the real next prime number -- use primality testing\n1# NextPrime(_i) <--\n[\n        Until(IsPrime(i)) i := NextPseudoPrime(i);\n        i;\n];\n\n";
