        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\n10 # StirlingNumber2(n_IsInteger,0) <-- If(n=?0,1,0);\n20 # StirlingNumber2(n_IsInteger,k_IsInteger) <-- Sum(i,0,k-1,(-1)^i*BinomialCoefficient(k,i)*(k-i)^n)/ k! ;\n\n";
