        scriptMap.put("PAdicExpand",scriptString);

        scriptString = new String[3];
        scriptString[0] = null;
        scriptString[1] = "\n10 # PAdicExpandInternal(0,_y) <-- {};\n20 # PAdicExpandInternal(_x,_y) <--\n[\n  Modulo(x,y) : PAdicExpandInternal(Quotient(x,y),y);\n];\n\n";
