        scriptString[1] = "\n/* Generate a random polynomial */\n\nRandomPoly(_var,_degree,_coefmin,_coefmax) <--\n  NormalForm(UniVariate(var,0,RandomIntegerList(degree+1,coefmin,coefmax)));\n\n";
