        scriptString[1] = "\nRandVerifyArithmetic(_n)<--\n[\n While(n>?0)\n [\n   n--;\n   VerifyArithmetic(FloorN(300*Random()),FloorN(80*Random()),FloorN(90*Random()));\n ];\n];\n\n";
