        scriptString[1] = "\nVerifyDiv(_u,_v) <--\n[\n  Local(q,r);\n  q:=Quotient(u,v);\n  r:=Rem(u,v);\n\n  Verify(Expand(u),Expand(q*v+r));\n];\n\n";
